//validate response nodes
import com.eviware.soapui.support.XmlHolder
def responseHolder = messageExchange.response.responseContent
def parsedResponse = new XmlHolder(responseHolder)
parsedResponse.namespaces["ns1"] = "http://tempuri.org/"
def result = parsedResponse.getNodeValue("//ns1:MultiplyResponse/ns1:MultiplyResult")
assert result == "2"

